const gitbookInjection = require('./../lib');

/**
 * [should: test framework module]
 * @type {Object}
 */
const should = require('chai').should();

describe('tests', function () {
    it('test case 1', function () {
        gitbookInjection.test({
            'css': [
                'index.css',
                './../custom.css'
            ]
        }).should.equal('<link rel="stylesheet" type="text/css" href="index.css"><link rel="stylesheet" type="text/css" href="./custom.css">');
    });

    it('test case 2', function () {
        gitbookInjection.test({
			'js': [
                'index.js',
                'custom.js'
            ]
        }).should.equal('<script type="text/javascript" src="index.js"></script><script type="text/javascript" src="custom.js"></script>');
    });

    it('test case 3', function () {
        gitbookInjection.test({
			'js': [],
            'css': []
        }).should.equal('');
    });
});
